import React from 'react';
import "./App.css"
import {createMuiTheme, ThemeProvider} from '@material-ui/core/styles';
import Checkbox from '@material-ui/core/Checkbox';
import {orange, red} from '@material-ui/core/colors';
import Button from "@material-ui/core/Button";
import FormControl from "@material-ui/core/FormControl";

const outerTheme = createMuiTheme({
    palette: {
        primary: {
            main: red[500],
        },
        secondary: {
            main: orange[500],
        },
    },
});


class Editor extends React.Component {
    state = {
        bg: "___",
        foreground: "Yay"
    };

    render() {
        let fontFamily = "'Ubuntu Mono', monospace";

        const absPos = {
            position: "absolute",
            top: 100, left: 0,
            fontFamily,
            color: "blue",
            fontSize: 14,
            background: "transparent",
            border: 0
        };
        let AsciiField = props =>
            <div
                style={absPos}>
                {props.label}
                <FormControl
                    component="textarea"
                    autoComplete="false"
                    rows="20"
                    margin="normal"
                    {...props} />
            </div>;

        return (
            <ThemeProvider theme={outerTheme}>
                <div style={{fontFamily: "'Ubuntu Mono', monospace"}}>
                    <h1>Ascii Editor</h1>

                    <Checkbox defaultChecked/>
                    <Checkbox defaultChecked/>
                    <Button/>

                    <AsciiField
                        defaultValue={this.state.bg}
                        onChange={x => this.setState({bg: x.target.value})}
                        label="BG"
                    />
                    <AsciiField
                        label={<span
                            style={{marginLeft: 32}}>Foreground</span>}
                        defaultValue={this.state.foreground}
                        onChange={x => this.setState({foreground: x.target.value})}
                    />
                </div>
            </ThemeProvider>
        );
    }
}

function App() {
    return (
        <Editor/>
    );
}

export default App;
