import React from 'react';
import "./App.css"
import {createMuiTheme, ThemeProvider} from '@material-ui/core/styles';
import Checkbox from '@material-ui/core/Checkbox';
import {orange, red} from '@material-ui/core/colors';
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";

const outerTheme = createMuiTheme({
    palette: {
        primary: {
            main: red[500],
        },
        secondary: {
            main: orange[500],
        },
    },
});


class Editor extends React.Component {
    state = {
        bg: "___",
        foreground: "Yay"
    };

    render() {
        let fontFamily = "'Ubuntu Mono', monospace";

        const styles = {
            container: {
            },
            textField: {
                position: "absolute", top: 100, left: 0
            },
            myFont:{
                fontFamily
            },
        }
        return (
            <ThemeProvider theme={outerTheme}>
                <div style={{fontFamily: "'Ubuntu Mono', monospace"}}>
                    <h1>Ascii Editor</h1>

                    <Checkbox defaultChecked/>
                    <Checkbox defaultChecked/>
                    <Button/>

                    <TextField
                        value={this.state.bg}
                        onChange={x => this.setState({bg: x.target.value})}
                        label="BG"

                        className={styles.textField}
                        autoComplete="false"
                        multiline
                        rows="20"
                        margin="normal"
                        InputProps={{
                            classes: {
                                input: styles.myFont
                            }
                        }}/>
                    <TextField
                        label={<span
                            style={{marginLeft: 32}}>Foreground</span>}
                        value={this.state.foreground}
                        onChange={x => this.setState({foreground: x.target.value})}

                        className={styles.textField}
                        autoComplete="false"
                        multiline
                        rows="20"
                        margin="normal"
                        InputProps={{
                            classes: {
                                input: styles.myFont
                            }
                        }}/>
                </div>
            </ThemeProvider>
        );
    }
}

function App() {
    return (
        <Editor/>
    );
}

export default App;
